import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import uiSlice from './store/ui-slice';
import { useSelector,useDispatch } from 'react-redux';
import { useEffect } from 'react';
import { uiActions } from './store/ui-slice';
import { Fragment } from 'react';
import Notification from './components/UI/Notification';
//this isInitial variable will not be initialized if the componet renders again
//  we do not want the empty cart to overwrite the cart in database when useeffect runs the first time
let isInitial = true;
function App() {
  const dispatch = useDispatch()
  const showCart = useSelector(state => state.ui.cartIsVisible)
  const cart = useSelector((state) => state.cart)
  const notification = useSelector(state=> state.ui.notification)
  useEffect(()=>{ // dont write async with round brancket in this line , else create new function
    const sendCartData = async() => {
      dispatch(uiActions.setNotification({
        status: "pending",
        title: "sending...",
        message: "Sending cart data!"
      }))
      const response = await fetch('https://redux-f30bf-default-rtdb.firebaseio.com/cart.json', {
        method: 'PUT', body: JSON.stringify(cart)} // put will update the existing data unlike post that will add the data in the list
    )
    if(!response.ok){ //if the there was an error
      throw new Error('sending cart data failed')
    }
    // code will make part this line if there is no error above

    dispatch(uiActions.setNotification({
      status: "success",
      title: "Success",
      message: "Sent cart data successfully!"
    }))
  }
  sendCartData().catch(error=>{
    dispatch(uiActions.setNotification({
      status: "Error",
      title: "Error!!",
      message: "Sending cart data failed!"
    }))
  })
}, [cart])
  return (
    <Fragment>
      {notification && <Notification status={notification.status} title={notification.title} message={notification.message} />}
      <Layout>
        {showCart && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
